#include <string>
using namespace std;

int min_characters_changes(string &s, int k){
    map<char, int> letter_freq;
    for(int i=0; i<s.size(); ++i){
        char c = s[i];
        letter_freq[c] += 1;
    }

    int total_chars_diff = 0;
    for (auto entry : letter_freq){
        if(entry.second == 1){
            total_chars_diff += 1;
        }
    }

    int nb_chars_diff = 0;
    int letters = 0;
    for(int i=0; i<s.size(); ++i){
        char c = s[i];
        if(letter_freq[c] > 1){
            nb_chars_diff += 1;
        }
        else if (letter_freq[c] == 1){
            nb_chars_diff += 1;
            letters += 1;
        }
    }

    int number_of_chars_diff = 0;

    if(nb_chars_diff + total_chars_diff == 0)
    {
        number_of_chars_diff = letters >= k? 0: 1;
    }
    else if(nb_chars_diff + total_chars_diff >= k){
        number_of_chars_diff = nb_chars_diff;
    }
    else{
        number_of_chars_diff = 1;
    }
    return number_of_chars_diff;


}

int main()
{
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);

    string s;
    cin >> s;

    int k;
    cin >> k;

    int result = min_characters_changes(s, k);
    cout << result;
    return 0;
}

