{
    for (int i = 1; i <= n; ++i)
    {
        for (int j = 1; j <= a; ++j)
        {
            int l = p[i].left, r = p[i].right, t = p[i].up, b = p[i].down;
            if (l > r || t > b) continue;
            for (int k = max(l, b + 1); k <= min(r, a + t - 1); ++k)
            {
                if (f[k] == (j % 2? 1 : 0) || f[k + 1] == (j % 2? 1 : 0))
                    return false;
            }
            for (int k = max(t, l + 1); k <= min(b, r - 1); ++k)
            {
                if (f[k * a + j] == (j % 2? 1 : 0) || f[k * a + j + 1] == (j % 2? 1 : 0))
                    return false;
            }
        }
    }
    return true;
}
