    for (int i = 0; i < n; ++i)
    {
        int l, r, t, b;
        scanf("%d%d%d%d", &l, &r, &t, &b);
        p[i + 1] = Node(l, r, t, b);
    }
    for (int i = 1; i <= a * b; ++i)
    {
        f[i] = 1;
        if (i % 2 == 0 && (i / 2) % 2 == 0)
            f[i] = 0;
    }
    for (int i = 1; i <= n; ++i)
    {
        for (int j = 1; j <= a; ++j)
        {
            int l = p[i].left, r = p[i].right, t = p[i].up, b = p[i].down;
            if (l > r || t > b) continue;
            f[max(l, b + 1)][min(r, a + t - 1)] = f[min(r, a + t - 1)][max(l, b + 1)] = (j % 2? 1 : 0);
            f[max(t, l + 1) * a + j][min(b, r - 1) * a + j + 1] = f[min(b, r - 1) * a + j + 1][max(t, l + 1) * a + j] = (j % 2? 1 : 0);
