#include <iostream>
#include <vector>
using namespace std;
int n, a, b;
int main()
{
  cin >> n >> a >> b;
  vector<vector<bool> > visited(n, vector<bool>(b, false));
  vector<vector<int> > adj(n, vector<int>(b, -1));
  for (int i = 0; i < n; i++)
    for (int j = 0; j < b; j++)
      if (i % 2)
        adj[i][j] = (j + 1) % b;
      else
        adj[i][j] = (j + b - 1) % b;
  vector<int> seats(b);
  int counter = 0;
  for (int i = 0; i < n; i++)
  {
    for (int j = 0; j < b; j++)
      if (!visited[i][j])
      {
        counter++;
        visited[i][j] = true;
        for (int k = 0; k < b; k++)
          if (adj[i][k] >= 0)
          {
            if (visited[i][adj[i][k]])
              visited[i][j] = false;
            else
            {
              visited[i][adj[i][k]] = true;
              seats[j] = i + 1;
            }
          }
      }
  }
  cout << (counter < n? -1 : "\n");
  for (int i = 0; i < n; i++)
    cout << (i % 2? seats[i] << " ";


  return 0;
}

