#include <cctype>
#include <cstdlib>
using namespace std;

void printMismatch (string exp, string act) {
    bool isExpected = true;
    for(unsigned int i = 0; i < exp.length(); i++) {
        if (exp[i]!= act[i]) {
            isExpected = false;
            cout << "Type: " << ((islower(exp[i]))? 'c' : 'C') << endl;
            cout << "Mismatch: " << act.substr(i) << endl;
            break;
        }
    }
    if (isExpected) {
        cout << "Type: " << ((islower(exp[0]))? 'c' : 'C') << endl;
        cout << "Mismatch: " << act << endl;
    }
}

int main() {
    //input
