#include <iostream>
#include <cstring>
#include <cctype>
using namespace std;

int m, n, len;
char grid[111][111], answer[111];

void make_grid()
{
	cin >> n >> m;
	memset(grid, 0, sizeof(grid));
	for (int i = 0; i < n; i++)
	{
		for (int j = 0; j < m; j++)
			cin >> grid[i][j];
	}
}

void check(int x, int y, char& ans)
{
	int k = x;
	ans = '\0';
	for (; x >= 0 && y >= 0 && k >= 0; x--, y--, k--)
	{
		if (grid[x][y]!= grid[k][k])
		{
			ans = grid[k][k];
			return;
		}
	}
	if (ans == '\0')
	{
		ans = grid[k][k];
		return;
	}
}

int main()
{
	make_grid();
	for (int i = 0; i < n; i++)
	{
		for (int j = 0; j < m; j++)
		{
			if (grid[i][j] == '\0')
				continue;
			if (i!= n - 1 && grid[i + 1][j] == grid[i][j])
				continue;
			if (j!= m - 1 && grid[i][j + 1] == grid[i][j])
				continue;
			char ans = '\0';
			check(i, j, ans);
			if (ans == '\0')
				continue;
			answer[len++] = ans;
		}
	}
	for (int i = 0; i < len; i++)
		answer[i] = tolower(answer[i]);
	answer[len] = '\0';
	cout << answer << endl;
	return 0;
}
