                bool flag = true;
                queue<pair<int, int>> que;
                que.push({i, j});
                is_visited[i][j] = true;
                while (!que.empty())
                {
                    pair<int, int> cur = que.front();
                    que.pop();
                    for (int k = 0; k < 4; ++k)
                    {
                        pair<int, int> next;
                        next.first = cur.first + d[k][0];
                        next.second = cur.second + d[k][1];
                        if (next.first < 0 || next.first >= n || next.second < 0 || next.second >= m)
                            continue;
                        if (is_visited[next.first][next.second])
                            continue;
                        if (grid[next.first][next.second] == 'c')
                        {
                            flag = false;
                            break;
                        }
                        que.push(next);
                        is_visited[next.first][next.second] = true;
                    }
                    if (!flag)
                        break;
                }
                if (flag)
                {
                    for (int i = 0; i < n; ++i)
                        for (int j = 0; j < m; ++j)
                            if (grid[i][j]!= 'c' &&!is_visited[i][j])
                                ans += grid[i][j];
                    for (int i = 0; i < n; ++i)
                        for (int j = 0; j < m; ++j)
                            if (grid[i][j] == 'c')
                                is_visited[i][j] = false;
                    printf("%s\n", ans.c_str());
                }
                else
                    printf("empty answer\n");
            }
    return 0;
}

