    bool is_lucky_number(const string&amp; str)

    {

        for (int i = 0; i &lt; str.size(); i++)

        {

            if (str[i] == '4' || str[i] == '7')

                return true;

        }

        return false;

    }

    bool is_smallest_lucky_number(const string&amp; str, int pos)

    {

        //Check if the number is a lucky number

        if (!is_lucky_number(str))

            return false;

        //Check if there are more lucky numbers ahead of the current position

        for (int i = pos + 1; i &lt; str.size(); i++)

            if (is_lucky_number(str.substr(i, str.size() - i)))

                return false;

        return true;

    }

    string smallest_lucky_number(const string&amp; str)

    {

        string smallest_lucky = "";

        for (int i = 0; i &lt; str.size(); i++)

        {

            if (is_smallest_lucky_number(str, i))

                smallest_lucky = str.substr(i, str.size() - i);

        }

        return smallest_lucky;

