// Program to tell whether Peter sees 2 sequences of flags colors.
// Given a string denoting the colors of the flags seen during the journey.
// The train is coming from city A to city B, and Peter is asleep
// He wakes up and writes the sequence of the colors that he has seen
// After the second period of sleep, he again writes the sequence
// Then he is told that he wakes up at a station
// He has to decide if he has seen the sequences on the journey from A to B or from B to A

// The flag colors can only be one of lower case letters (a,b,c,d,e....),
// hence we keep an array of size 26

// We take two arrays and keep filling them while the colors are being
// seen by the train.

#include <iostream>
#include<cstring>
#include<cstdlib>
using namespace std;

void process(char s[], int arr[], int len)
{
    for (int i = 0; i < len; i++)
        arr[s[i] - 'a']++;
}

// We assume that the string color[] will contain only lower case letters
void solution(char *color)
{
    // We keep two arrays of size 26
    int flag_forward[26]; // for storing all the colors seen forward
    int flag_backward[26]; // for storing all the colors seen backward

    // We will fill the arrays while the train is coming from city A to B
    // And then we will fill the second array while the train is coming from B to A
    process(color, flag_forward, strlen(color));

    // We reverse the string and process the colors
    // so as to fill the other array
    char *p, temp;
    for (p = color + 1; p < color + strlen(color); ++p)
        temp = *p, *p = *color, *color++ = temp;
    process(color, flag_backward, strlen(color));

    // For every color we compare the forward and backward arrays
    // if they have the same number of colors for that color, then we
    // know we have seen a color at least twice
    // therefore the flag is "fantasy"
    int i;
    bool flag = false;
    for (i = 0; i < 26; i++)
        if (flag_backward[i]!= flag_forward[i])
            flag = true;

    if (flag)
        cout << "fantasy\n";
    else
    {
        if (flag_forward[0] > 0)
            cout << "forward\n";
        if (flag_backward[0] > 0)
            cout << "backward\n";
        if (flag_backward[0] && flag_forward[0])
            cout << "both\n";
    }
}
