// This function calculates the maximum value of the array and stores its index in array indexMax
void calculateMax(int *arr, int n, int *indexMax)
{
    int max = arr[0];
    for (int i = 1; i < n; i++)
    {
        if (arr[i] > max)
        {
            max = arr[i];
            *indexMax = i;
        }
    }
}

// This function calculates the sum of the rest of the numbers in the array, after the indexMax value
int calculateSum(int *arr, int n, int indexMax)
{
    int sum = 0;
    for (int i = indexMax + 1; i < n; i++)
    {
        sum += arr[i];
    }
    return sum;
}

int main()
{
    int n; // Length of the array
    cin >> n;
    int length[n]; // Array for storing the lengths of the worms
    int indexMax = -1;

    for (int i = 0; i < n; i++)
    {
        cin >> length[i];
    }
    // The first step is to determine the maximum value in the array
    calculateMax(length, n, &indexMax);
    // If the maximum value occurs more than twice, then we cannot obtain a valid output
    if (indexMax == -1)
    {
        cout << -1 << endl;
    }
    else
    {
        // The maximum value appears twice. We get the index of the third value.
        int indexRest = -1;
        // We are looking for the value less than the maximum, but greater than the maximum.
        for (int i = 0; i < n; i++)
        {
            if ((length[i] > length[indexMax]) && (length[i] < length[indexMax] * 2))
            {
                indexRest = i;
            }
        }
        // If the maximum value is equal to the maximum * 2, then the sum of the rest of the values will be equal to zero.
        if (length[indexMax] == length[indexMax] * 2)
        {
            cout << -1 << endl;
        }
        else
        {
            cout << indexMax + 1 << " " << indexRest + 1 << " " << length[indexMax] - length[indexRest] + 1 << endl;
        }
    }
