#include <iostream>
#include <cctype>

std::string change(std::string word) {
    int up_case = 0, low_case = 0;

    for (int i = 0; i < word.length(); i++) {
        if (std::islower(word[i])) low_case++;
        if (std::isupper(word[i])) up_case++;
    }

    if (low_case > up_case) {
        for (int i = 0; i < word.length(); i++) word[i] = std::tolower(word[i]);
        return word;
    }

    if (up_case > low_case) {
        for (int i = 0; i < word.length(); i++) word[i] = std::toupper(word[i]);
        return word;
    }

    int start_index = 0;
    int max_change = 0;
    for (int i = 0; i < word.length(); i++) {
        if (i - 1 < 0 or std::islower(word[i - 1])) {
            int temp_change = 0;
            for (int j = i; j < word.length(); j++) {
                if (std::islower(word[j])) temp_change++;
                if (std::isupper(word[j])) break;
            }
            if (temp_change > max_change) {
                start_index = i;
                max_change = temp_change;
            }
        }
    }

    int end_index = 0;
    max_change = 0;
    for (int i = 0; i < word.length(); i++) {
        if (i + 1 == word.length() or std::islower(word[i + 1])) {
            int temp_change = 0;
            for (int j = i; j >= 0; j--) {
                if (std::islower(word[j])) temp_change++;
                if (std::isupper(word[j])) break;
            }
            if (temp_change > max_change) {
                end_index = i;
                max_change = temp_change;
            }
        }
    }

    int start_change = 0, end_change = 0;
    for (int i = start_index; i <= end_index; i++) start_change++;
    for (int i = start_index; i >= 0; i--) end_change++;

    if (start_change > end_change) {
        for (int i = 0; i < word.length(); i++) word[i] = std::toupper(word[i]);
    } else {
        for (int i = 0; i < word.length(); i++) word[i] = std::tolower(word[i]);
    }
    return word;
}

int main() {
    std::string word;
    std::getline(std::cin, word);
    std::cout << change(word);
}
