#include <iostream>
#include <math.h>
using namespace std;
#define ll long long
#define fr first
#define sc second
#define pii pair<int,int>
#define pll pair<ll,ll>

int N, M, K;
int arr[2000][2000];
bool visited[2000][2000];
int tree[2000][2000];
int time;

struct node
{
    pll v;
    ll h;
    ll l;
    ll r;
    node* ln;
    node* rn;
    node(pll v, ll h, ll l, ll r)
    {
        this->v = v;
        this->h = h;
        this->l = l;
        this->r = r;
        this->ln = NULL;
        this->rn = NULL;
    }

    void insert(node*& head, pll v, ll h, ll l, ll r)
    {
        node* temp = new node(v, h, l, r);
        if (head == NULL)
        {
            head = temp;
            return;
        }
        node* ptr = head;
        while (ptr->h >= h)
            ptr = ptr->ln;

        if (temp->l >= head->l)
        {
            temp->ln = head;
            head = temp;
        }
        else
        {
            temp->ln = ptr;
            ptr->l = temp;
        }
    }

    void split(node*& head, node*& tail, pll v, ll& cnt, ll h)
    {
        if (head == NULL)
        {
            node* temp = new node(v, h, 0, 0);
            head = temp;
            tail = temp;
            return;
        }
        node* ptr = head;
        while (ptr->h >= h)
            ptr = ptr->ln;
        cnt = 0;

        while (ptr!= NULL && ptr->h == h)
        {
            cnt += ptr->l;
            ptr = ptr->rn;
        }
    }

    void insert_time(pll v, ll h)
    {
        node* ptr = head;
        while (ptr->h < h)
            ptr = ptr->rn;

        insert(head, v, h, 0, 0);
        insert(tail, v, h, time, time);
    }

    void split_time(pll v, ll h, ll& cnt)
    {
        node* ptr = head;
        ll left = 0, right = 0;
        split(head, tail, v, cnt, h);
        while (ptr!= NULL)
        {
            if (cnt >= ptr->l && cnt <= ptr->r)
            {
                left = min(ptr->l, cnt);
                right = max(ptr->l, cnt);
                break;
            }
            else
            {
                left = ptr->l;
                right = ptr->r;
            }
            ptr = ptr->rn;
        }
    }

    node* find_leaf(node* head, node* tail, int& cnt)
    {
        node* ptr = head;
        while (ptr!= tail)
        {
            cnt += ptr->l;
            ptr = ptr->rn;
        }
        return ptr;
    }

    void find(pll v, ll h, ll& cnt)
    {
        node* ptr = head;
        while (ptr->h >= h)
            ptr = ptr->ln;

        if (ptr == NULL)
        {
            cnt = 0;
            return;
        }

        cnt = 0;

        while (ptr!= NULL && ptr->h == h)
        {
            cnt += ptr->l;
            ptr = ptr->rn;
        }
    }

    void remove(node*& head, node*& tail, node* ptr)
    {
        if (ptr == NULL)
            return;
        if (ptr->l > 0)
        {
            node* tempp = ptr->ln;
            if (tempp->h == ptr->h)
                remove(head, tail, tempp);
            else
                remove(head, tail, find_leaf(head, tail, tempp->l));
            ptr->ln = tempp->ln;
            tempp->ln->rn = ptr;
            delete tempp;
            if (ptr->rn == tail)
                tail = ptr;
        }
        else
        {
            node* tempp = ptr->rn;
            if (tempp->h == ptr->h)
                remove(head, tail, tempp);
            else
                remove(head, tail, find_leaf(head, tail, tempp->l));
            ptr->rn = tempp->rn;
            tempp->rn->ln = ptr;
            delete tempp;
            if (ptr->ln == tail)
                tail = ptr;
        }
    }
}

int main()
{
    cin >> N >> M;

    for (int i = 1; i <= N; i++)
    {
        for (int j = 1; j <= M; j++)
            cin >> arr[i][j];
    }
