                        break;
                    ptr = ptr->rn;
                }
                if (ptr == NULL)
                {
                    ptr = new node(make_pair(i, j), 0, 0, 0);
                    insert(head, make_pair(i, j), 0, 0, 0);
                }
            }
        }
    }
    for (int i = 0; i < K; i++)
    {
        int x, y;
