using namespace std;


void addCordinates(long long &x, long long &y, long long &x2, long long &y2){
    long long dx = x - x2;
    long long dy = y - y2;
    dx = (dx + abs(dx)) / 2;
    dy = (dy + abs(dy)) / 2;
    x += dx;
    y += dy;
}


int main(){
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    int M, N, K;
    cin >> N >> M >> K;

    // Initialize array of M+1 x N+1
    long long m[M+1][N+1] = { 0 };

    // Put value of trees
    for (int i = 1; i <= N; i++){
        for (int j = 1; j <= M; j++){
            m[i][j] = -1;
        }
    }
    for (int i = 1; i <= K; i++){
        int x, y;
        cin >> x >> y;
        m[x][y] = i;
    }

    // Fire
    long long x, y;
    cin >> x >> y;
    int count = 1;
    while (x >= 1 && y >= 1 && x <= N && y <= M){
        m[x][y] = count;
        if (count == K){
            break;
        }
        long long dx = x - 1, dy = y;
        long long new_x = x - (dx + abs(dx)) / 2;
        long long new_y = y + (dy + abs(dy)) / 2;
        addCordinates(x, y, new_x, new_y);
        if (new_x < 1 || new_y < 1 || new_x > N || new_y > M){
            break;
        }
        if (m[new_x][new_y] == -1){
            count++;
        }
        else {
            break;
        }
    }
    if (m[x][y] == K){
        cout << x << " " << y << endl;
    }
    else {
        cout << "-1 -1" << endl;
    }

    return 0;
}
