int get_railway(Railway railway[], int u, int v) {
    for (int i = 0; i < 13; i++) {
        if (railway[i].u == u && railway[i].v == v) {
            return i;
        }
    }
    return -1;
}

int get_next_railway(Railway railway[], int u, int v, int cur) {
    int next = get_railway(railway, u, v);
    return next == cur? -1 : next;
