int get_time_railway(Railway railway[], int u, int v) {
    int time = 0;
    int next = u;
    while (1) {
        time++;
        next = get_next_railway(railway, u, v, next);
        if (next == -1) {
            break;
        }
        u = next;
    }
    return time;
}

