#include <bits/stdc++.h>
#define N 401
#define INF 0x3f3f3f3f
using namespace std;
int n, m, d[N], vis[N];
struct Edge{
    int v, nex;
}e[N*N];
int q[N], qt, qs, qe;
void addEdge(int u, int v){
    e[++qt] = (Edge){v, d[u]};
    d[v] = d[u] + 1;
}
int spfa(int start){
    qt = qs = qe = 0;
    vis[start] = 1;
    q[qt++] = start;
    while(qs <= qe){
        int cur = q[qs++], h = 0;
        for(int i = d[cur]; i; i = e[i].nex){
            int v = e[i].v;
            if(!vis[v]){
                vis[v] = 1;
                h++;
                q[qt++] = v;
            }
            if(d[v] == 1 || d[v] > d[cur] + 1){
                d[v] = d[cur] + 1;
                if(v!= start){
                    for(int j = e[i].nex; j; j = e[j].nex){
                        int w = e[j].v;
                        e[j].nex = e[w].nex;
                        e[w].nex = j;
                    }
                }
            }
        }
        vis[cur] = 0;
    }
    int res = INF;
    for(int i = 1; i <= n; ++i){
        res = min(res, d[i]);
    }
    return res;
}
