int dist[N][N * N];
queue<int> q;
bool spfa(int k, Road* r, int *cnt, int start){
    int u, i;
    memset(used, false, sizeof(used));
    memset(dist, 0x3f, sizeof(dist));
    dist[start][start] = 0;
    q.push(start);
    used[start] = true;
    while(!q.empty()){
        u = q.front();
        q.pop();
        used[u] = false;
        int start_x = u / n, start_y = u % n;
        for(i = 0; i < cnt[u]; i++){
            int p = r[u * cnt[u] + i].x * n + r[u * cnt[u] + i].y;
            int d = dist[u][start_x] + dist[u][start_y] + (r[u * cnt[u] + i].type == 1? 1 : 0);
            if(d < dist[p][start_x] + dist[p][start_y]){
                dist[p][start_x] = dist[p][start_y] = d;
                if(used[p]){
                    q.push(p);
                } else {
                    q.push(p);
                    used[p] = true;
                }
            }
        }
    }
    for(i = 0; i <= n * (n - 1); i++){
        if(dist[k][i] < INF){
            if(dist[k][i] + dist[i][i] < ans){
                ans = dist[k][i] + dist[i][i];
            }
        }
    }
    return dist[k][k] < INF && ans >= 0;
}
