        if (v[j].size() - k - 1 >= 0) {
          med += v[j][v[j].size() - k - 1];
        }
        skw += abs(m - med);
      }

      if (skw > ans[0]) {
        ans[0] = skw;
        ans[1] = mx;
        ans[2] = m;
        ans[3] = med;
      }
    }
  }

  for (int i = 1; i <= ans[1]; i++) {
    cout << ans[2] - v[ans[0]][ans[3] - i] << " ";
  }

  return 0;
}


