using namespace std;
typedef long long LL;

LL m;
int n;
int ar[205];
int sum[205];
int ans;
int dp[205][205];

int solve(int start, int end) {
    if (start > end) {
        return 0;
    }
    if (start == end) {
        return dp[start][end] = 1;
    }
    int mx = 0, mn = 1e9;
    for (int i = start; i <= end; i++) {
        mx = max(mx, sum[i]);
        mn = min(mn, sum[i]);
    }
    if (mx - mn > m) {
        return 0;
    }
    LL ret = 0;
    for (int i = start; i <= end; i++) {
        ret += (LL)solve(start, i - 1) * solve(i + 1, end) % 1000000007LL;
        ret %= 1000000007LL;
    }
    return dp[start][end] = ret;
}

