double C(int n, int k) {
double res = 1.0;
for(int i=1; i <= k; i++)
res = res * (n-i+1) / i;
return res;
}

double M(int n, int k) {
int i,j;
double res = 0.0;
for(i=0; i<k; i++)
for(j=i; j<k; j++) {
if(i==0) res += (2*j+1)/(2*n);
else if(j==0) res += (n-1)/(2*n);
else res += (n-i-j-1)/(2*n);
}
return res;
}

double P(int n, int k) {
double r = M(n, k)/C(n,k);
return r;
}

