class Truth {
  public:
    Truth(const std::vector<std::string>& input);

    void process();

    friend std::ostream& operator<<(std::ostream& out, Truth& t);

  private:
    std::vector<std::string> _input;
};

Truth::Truth(const std::vector<std::string>& input) : _input{input} {}

void Truth::process() {
  if (_input.empty()) {
    return;
  }
  int m{std::stoi(_input[0])};
  std::vector<bool> is_true{_input[1] == "+"};
  int i{2};
  while (i < _input.size()) {
    int j{i + 1};
    while (j < _input.size() && _input[i] == _input[j]) {
      ++j;
    }
    for (int k = i; k < j; ++k) {
      is_true[std::stoi(_input[k].substr(1))] ^= is_true[std::stoi(_input[k])];
    }
    i = j;
  }
  for (const auto& i : is_true) {
    std::cout << i? "Truth" : "Lie" << "\n";
  }
}

std::ostream& operator<<(std::ostream& out, Truth& t) {
  for (const auto& i : t._input) {
    out << i << "\n";
  }
  return out;
}

