import java.util.*;

public class Main{
    public static void main(String[] args){
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt(), k = sc.nextInt();
        int num[] = new int[n];
        int tmp[][] = new int[n][k];
        for(int i = 0; i < n; ++i){
            num[i] = sc.nextInt();
        }
        if(can(n, k, num, tmp)){
            System.out.println("YES");
            for(int i = 0; i < n; ++i){
                System.out.print(tmp[i][0] + " ");
            }
            System.out.println();
        }
        else System.out.println("NO");
    }
    static boolean can(int n, int k, int num[], int tmp[][]){
        int pos[] = new int[k];
        for(int j = 0; j < k; ++j){
            pos[j] = j;
        }
        for(int j = 0; j < n; ++j){
            tmp[j][0] = j % k;
        }
        for(int j = 1; j < k; ++j){
            boolean flag = true;
            for(int i = 0; i < n; ++i){
                if(num[i] == pos[j]){
                    tmp[i][j] = j;
                    flag = false;
                }
            }
            for(int i = 0; i < n - 1; ++i){
                for(int l = i + 1; l < n; ++l){
                    if((num[i] == num[l] && num[i]!= pos[j]) || (num[i] == pos[j] && num[l] == pos[j])){
                        int tmp1 = num[i];
                        num[i] = num[l];
                        num[l] = tmp1;
                    }
                }
            }
            if(flag){
                for(int l = 0; l < n; ++l){
                    if(num[l] == pos[j]){
                        tmp[l][j] = j;
                    }
                }
                return true;
            }
            for(int i = 0; i < n - 1; ++i){
                for(int l = i + 1; l < n; ++l){
                    if(num[i] == num[l]){
                        int tmp1 = num[i];
                        num[i] = num[l];
                        num[l] = tmp1;
                    }
                }
            }
        }
        return false;
    }
}

