    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt(), k = sc.nextInt();
        int a[] = new int[n];
        for (int i = 0 ; i < n ; i++) a[i] = sc.nextInt();
        int min[] = new int[n];
        for (int i = 0 ; i < k ; i++) min[i] = n + 1;
        boolean b[] = new boolean[n];
        for (int i = 0 ; i < n ; i++) b[i] = false;
        int color[] = new int[n];
        for (int i = 0 ; i < n ; i++) color[i] = 0;
        int c = 0;
        while (!isPossible(min, b)) {
            if (min[c] == n + 1) {
                c++;
                continue;
            }
            b[min[c]] = true;
            color[min[c]] = c + 1;
            min[c] = n + 1;
            c = 0;
        }
        if (isPossible(min, b)) {
            System.out.println("YES");
            for (int i = 0 ; i < n ; i++) System.out.print((color[i] == 0? "0" : color[i]) + " ");
        } else
            System.out.println("NO");
    }
    static boolean isPossible(int min[], boolean b[]) {
        for (int i = 0 ; i < min.length ; i++)
            if (!b[i] && min[i]!= n + 1)
                return false;
        return true;
    }
}

