public class problem63b
{
    public static void main(String[] args) 
    {
    	int n, k;

    	Scanner input = new Scanner(System.in);
    	n = input.nextInt();
    	k = input.nextInt();

    	int a[] = new int[n];
    	for(int i = 0; i < n; i++)
    	{
    		a[i] = input.nextInt();
    	}

    	int c[] = new int[n];
    	boolean can = false;
    	int start = 1;
    	int first = 1;
    	int prev = -1;

    	for(int i = 0; i < n; i++)
    	{
    		if(first == a[i])
    		{
    			c[i] = start;
    			start++;
    			prev = a[i];
    		}
    		else
    		{
    			int rem = k - (start - 1);
    			if(rem > 0)
    			{
    				for(int j = 1; j <= rem; j++)
    				{
    					if(a[i] == prev + j)
    					{
    						c[i] = start;
    						prev = a[i];
    						start++;
    						j = rem + 1;
    					}
    				}
    			}
    			else
    			{
    				can = false;
    			}
    		}

    		if(c[i] == 0)
    			can = false;
    	}

    	if(can)
    		System.out.print("YES\n");
    	else
    		System.out.print("NO\n");

    	for(int i = 0; i < n; i++)
    		System.out.print((c[i] == 0? 1 : c[i]) + " ");

    }
}

