			for(int j = 0 ; j < col.length ; j++) {
				if(col[j] == a[i]) {
					ok = true;
					break;
				}
			}
			if(!ok) {
				for(int j = 0 ; j < col.length ; j++) {
					if(col[j] == 0) {
						col[j] = a[i];
						ok = true;
						break;
					}
				}
			}
			if(!ok) {
				for(int j = 0 ; j < col.length ; j++) {
					if(col[j] == a[i]) {
						used = false;
					}
				}
				if(used) {
					col[0] = a[i];
				}
			}
		}

		if(used) {
			System.out.println("YES");
