public class Main{

    public static void main(String[] args){
        Scanner in = new Scanner(System.in);
        int n = in.nextInt();
        int[][] a = new int[n][2];
        int[] count = new int[101];
        int[][] dp = new int[n][2];

        for(int i=0;i<n;i++){
            a[i][0] = in.nextInt();
            a[i][1] = in.nextInt();
            count[a[i][0]]++;
            count[a[i][1]]++;
        }

        for(int i=0;i<n-1;i++){
            dp[i][0] = count[a[i][0]];
            dp[i][1] = count[a[i][1]];
        }

        dp[n-1][0] = count[a[n-1][0]];
        dp[n-1][1] = 1;

        for(int i=n-2;i>=0;i--){
            if(dp[i+1][0]>0) dp[i][0] += dp[i+1][0];
            if(dp[i+1][1]>0) dp[i][1] += dp[i+1][1];
        }

        for(int i=0;i<n;i++){
            for(int j=0;j<2;j++){
                int c = 0;
                while(a[i][j] == a[c][j]) c++;
                int min = Math.min(dp[c][0], dp[c][1]);
                min = Math.min(min, j+1);
                System.out.print(min+" ");
            }
        }
    }
}

