public class Solution {
    public static void main(String[] args) throws Exception {
        try (Input reader = new StandardInput()) {
            int t = reader.nextInt();
            while (t > 0) {
                int n = reader.nextInt(), k = reader.nextInt(), d = reader.nextInt();
                int[] days = reader.readIntArray();
                int max = 0;
                for (int i = 0; i < n; i++) {
                    int cur = 0, maxi = 0;
                    for (int j = i; j < n; j++) {
                        if (days[j] == days[i]) {
                            cur++;
                            maxi = Math.max(maxi, cur);
                            if (cur == d) {
                                max = Math.max(max, maxi);
                                cur = 0;
                            }
                        } else {
                            cur = 0;
                        }
                    }
                }
                max = Math.max(max, k - max);
                System.out.println(max);
                t--;
            }
        }
    }

    private static interface Input extends Closeable {
        String next() throws IOException;

        default int nextInt() throws IOException {
            return Integer.parseInt(next());
        }

        default long nextLong() throws IOException {
            return Long.parseLong(next());
        }

        default double nextDouble() throws IOException {
            return Double.parseDouble(next());
        }

        default int[] readIntArray() throws IOException {
            return readIntArray(nextInt());
        }

        default int[] readIntArray(int size) throws IOException {
            int[] array = new int[size];
            for (int i = 0; i < array.length; i++) {
                array[i] = nextInt();
            }
            return array;
        }
    }

    private static class StandardInput implements Input {
        private final Input reader = new Scanner(System.in);

        private StandardInput() throws IOException {
            reader.nextInt();
        }

        @Override
        public void close() throws IOException {
            reader.close();
        }

        @Override
        public String next() throws IOException {
            return reader.next();
        }
    }
}

