        var sc = new Scanner(System.in);
        var t = sc.nextInt();
        for (int _t = 0; _t < t; ++_t) {
            var n = sc.nextInt();
            var k = sc.nextInt();
            var d = sc.nextInt();
            var shows = new int[n];
            var count = 0;
            for (int _i = 0; _i < n; ++_i)
                shows[_i] = sc.nextInt();
            int ans = 1;
            for (int first = 0; first < k; ++first) {
                ans = Integer.MAX_VALUE;
                for (int second = 0; second < k; ++second) {
                    if (shows[0] == first && shows[n-1] == second) {
                        ans = 1;
                        break;
                    }
                    if (shows[0] == first && shows[n-d] == second) {
                        ans = 2;
                    }
                    if (shows[d-1] == first && shows[n-1] == second) {
                        ans = n-d+1;
                    }
                }
                if (ans < Integer.MAX_VALUE) {
                    break;
                }
            }
            System.out.println(ans);
        }
    }
}

