            arr[2] = Integer.parseInt(st.nextToken());
            arr[3] = Integer.parseInt(st.nextToken());
            int sum = 0;
            for(int val: arr){
                sum += val;
            }
            if((sum % 2)!= 0){
                writer.println("NO");
            }else{
                int div = sum/2;
                int count = 0;
                for(int val: arr){
                    if(val == div){
                        count++;
                    }
                }
                if(count == 4){
                    writer.println("YES");
                }else{
                    writer.println("NO");
                }
            }
        }
    }
}

