import java.util.*;

public class Solution{

    public static void main(String[] args){
        Scanner sc = new Scanner(System.in);
        String s = sc.next();
        int[] lps = new int[s.length()];
        int n = s.length();
        int m = 1;
        int p = 2;
        while(p<=n){
            if(s.charAt(p-1) == s.charAt(p)){
                p++;
                continue;
            }
            int q = p - m;
            if(p%2 == 1){
                q = p - 1 - m;
            }
            while(q>0 && q+m <= n && s.charAt(q) == s.charAt(q+m)){
                m = m + 1;
                q = p - m;
                if(p%2 == 1){
                    q = p - 1 - m;
                }
            }
            if(p%2 == 1){
                lps[p-1] = m;
            }else{
                lps[p] = m;
            }
            if(m > n/2){
                break;
            }
            m = 1;
            p = p + 2;
        }
        int q = 0;
        for(int i = 1;i<=n-1;i++){
            if(lps[i] == 0){
                continue;
            }
            if(lps[i] > q){
                q = lps[i];
            }
        }
        System.out.println(q);
    }
}

