public class Solution {
    public static void main(String[] args) {
        Scanner read = new Scanner(System.in);
        String s = read.nextLine();
        if(s.equals("")||s.length()<2){
            System.out.println(0);
            System.exit(0);
        }
        int start = 0;
        int max = 0;
        int[] check = new int[s.length()];
        for(int i=0;i<s.length();++i){
            check[i]=-1;
        }
        check[0] = 0;
        max = check[0];
        while(start+2<s.length()){
            if(check[start]>=0){
                check[start+1] = check[start];
                if(s.charAt(start+1)!= s.charAt(start+check[start])){
                    check[start+2] = check[start]+2;
                }
            }
            else if(s.charAt(start) == s.charAt(start+1)){
                check[start+1] = 1;
                check[start+2] = 2;
                if(start+1 == s.length()-1){
                    check[start+2]++;
                }
            }
            max = Math.max(max,check[start+1]);
            start++;
        }
        System.out.println(max);
    }
}

