                } else {
                    if (c == '#') isComment = true;
                    else if (c == '\n') {
                        return new String(buf, 0, cnt);
                    } else {
                        buf[cnt++] = (byte) c;
                    }
                }
            }
            return new String(buf, 0, cnt);
        }

