public class Solution {

    public static void main(String[] args) throws Exception {
        InputStreamReader reader = new InputStreamReader(System.in);
        BufferedReader buffer = new BufferedReader(reader);

        StringBuffer sb = new StringBuffer();
        char[] line = buffer.readLine().toCharArray();
        int k = Integer.parseInt(buffer.readLine());
        int[] count = new int[26];
        for (char c : line)
        {
            count[c - 'a']++;
        }

        int min = Integer.MAX_VALUE;
        for (int i = 0 ; i < 26 ; i++)
        {
            min = Math.min(count[i], min);
        }

        int ans = 0;
        if (min < k)
        {
            System.out.println("impossible");
        }
        else if (min == k)
        {
            System.out.println(0);
        }
        else
        {
            ans += min - k;
            for (int i = 0 ; i < 26 ; i++)
            {
                if (count[i] > k)
                {
                    ans += count[i] - k;
                }
            }

            System.out.println(ans);
        }

    }
}

import java.util.Scanner;

public class D322 {

	private static int[][] get_hist(String str) {
		int[][] hist = new int[26][2];
		for ( int i = 0; i < 26; i++ ) {
			hist[i] = new int[2];
		}
		for ( int i = 0; i < str.length(); i++ ) {
			hist[str.charAt(i) - 97][0]++;
			hist[str.charAt(i) - 97][1] = i;
		}
		return hist;
	}

	private static int get_min_cost(int[][] hist, int k) {
		int cost = 0;
		while ( k > 0 ) {
			int min_idx = 0;
			int min_cost = 100000;
			for ( int i = 0; i < 26; i++ ) {
				if ( hist[i][0] < min_cost ) {
					min_cost = hist[i][0];
					min_idx = i;
				}
			}
			if ( hist[min_idx][1] > cost ) {
				cost = hist[min_idx][1];
				hist[min_idx][0]--;
			} else {
				cost++;
				hist[min_idx][0]--;
				hist[min_idx][1]++;
			}
			k--;
		}
		return cost;
	}

	public static void main(String[] args) {
		Scanner in = new Scanner(System.in);
		String str = in.next();
		int k = in.nextInt();
		int[][] hist = get_hist(str);
		int cost = 0;
		if ( hist[0][0] >= k ) {
			System.out.print("0");
			return;
		} else if ( hist[0][0] + 1 >= k ) {
			cost += k - hist[0][0];
		} else {
			cost = get_min_cost(hist, k);
		}
		System.out.print(cost);
	}
}

