public class Main {
	public static void main(String[] args) throws IOException {
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		int N = Integer.parseInt(br.readLine());
		int[] cnt = new int[60];
		int index = 0;
		while (N > 0) {
			cnt[index] += N % 2;
			index++;
			N /= 2;
		}
		index--;
		while (index >= 0) {
			if (cnt[index] == 1) {
				System.out.print(index + " ");
			} else if (cnt[index] == 2) {
				System.out.print((index - 1) + " " + index + " ");
			} else if (cnt[index] == 3) {
				System.out.print(index + " " + (index - 1) + " " + (index - 2)
						+ " ");
			} else if (cnt[index] >= 4) {
				int tmp = cnt[index];
				int v = 0;
				for (v = 2; (v * v <= tmp); v++) {
					if ((tmp % v) == 0) {
						System.out
								.print((index + v) + " " + (index + v - 1)
										+ " ");
						tmp -= v;
						v--;
					}
				}
				if (tmp > 4) {
					System.out.print(index + " ");
				}
			}
			index--;
		}
	}
}

