public class HelloWorld
{
    public static void main(String[] args){
        Scanner sc=new Scanner(System.in);
        int n = sc.nextInt();
        int[] a = new int[n+1];
        int x = 1;
        int j = 1;
        while(n > 0){
            for(int i = 0; i < 1; i++){
                a[j] = x;
                j++;
            }
            for(int k = 0; k < n-1; k++){
                if(a[k] == a[k+1]){
                    a[k+1] = a[k+1] + 1;
                }
            }
            x++;
            n = n - 1;
        }
        for(int m = 1; m < a.length; m++){
            if(a[m]!= a[m-1]){
                System.out.print(a[m-1] + " ");
            }
        }
        System.out.print(a[a.length-1]);
    }
}

