public class Main {

	static boolean check(String s, int n) {
		for (int i = 0; i < n-1; i++) {
			if (s.charAt(i) == 'R' && s.charAt(i+1) == 'L')
				return false;
		}
		return true;
	}

	public static void main(String[] args) throws IOException {
		// TODO Auto-generated method stub
		BufferedReader stdin = new BufferedReader(new InputStreamReader(System.in));

		String nStr = stdin.readLine();
		int n = Integer.parseInt(nStr);

		String str = stdin.readLine();

		int l = 0;
		int r = n-1;

		int cnt = 0;

		while (l <= r) {
			if (check(str, n)) {
				if (str.charAt(l) == '.')
					str = str.replace(l, l+1, "L");
				if (str.charAt(r) == '.')
					str = str.replace(r, r, "R");

				l++;
				r--;
			} else {
				cnt++;
				str = str.replace(l, l+1, "L");
			}
		}

		int res = nStr.length() - cnt;

		System.out.println(res);

	}

}

