public class Name{
    static int p;
    static long q=1000000007;
    public static void main(String[]args){
        Scanner in=new Scanner(System.in);
        int n=in.nextInt(),m=in.nextInt(),max=1;p=1;
        String[]str=new String[n];
        for(int i=0;i<n;i++){str[i]=in.next();max=Math.max(max,str[i].length());}
        for(int i=1;i<n;i++){
            p=1;
            for(int j=0;j<m;j++){
                for(int k=0;k<n;k++){
                    if(str[k].charAt(j)<str[i].charAt(j)){p=Math.max(p,k);}
                }
            }
            String temp=str[i];str[i]=str[p];str[p]=temp;
        }
        for(int i=0;i<n;i++){for(int j=0;j<m;j++){if(i!=0 && j!=0){str[i]=str[i].substring(j)+str[i].charAt(j-1)+str[i].substring(j-1,j);}}}
        int[][]count=new int[m][max];
        for(int j=0;j<m;j++){
            for(int k=1;k<max;k++){
                int c=0;
                for(int l=0;l<n;l++){
                    if(str[l].length()>=k && str[l].charAt(k-1)<=str[l].charAt(k-1)){c++;}
                }
                count[j][k]=c;
            }
        }
        long sum=0;
        for(int k=0;k<max;k++){
            long cur=1;
            for(int j=0;j<m;j++){
                for(int i=0;i<count[j][k];i++){cur=(cur*(count[j][k]-i))%q;}
            }
            sum=(sum+(cur*p))%q;
            p*=n;p%=q;
        }
        System.out.println(sum);
    }
}

