    final static BigInteger MOD = BigInteger.valueOf(1000000007);
    final static int MAX_LENGTH = 100;

    public static void main(String[] args) 
    {
        BigInteger[][] table = new BigInteger[MAX_LENGTH][MAX_LENGTH];
        for (int i = 0; i < table.length; i++)
        {
            for (int j = 0; j < table[i].length; j++)
            {
                table[i][j] = BigInteger.valueOf(0);
            }
        }

        Scanner in = new Scanner(System.in);
        int n = in.nextInt();
        int m = in.nextInt();
        char[][] names = new char[n][m];
        for (int i = 0; i < n; i++)
        {
            String name = in.next();
            names[i] = name.toCharArray();
        }

        for (int i = 0; i < n; i++)
        {
            char[] name = names[i];
            for (int j = 0; j < name.length; j++)
            {
                table[j][name.length - 1 - j] = table[j][name.length - 1 - j].add(BigInteger.valueOf(1));
            }
        }

        for (int i = 0; i < m; i++)
        {
            for (int j = 0; j < m - i; j++)
            {
                table[j][j + i] = table[j][j + i].multiply(table[i][m - j - 1]).mod(MOD);
            }
        }

        BigInteger ans = BigInteger.valueOf(0);
        for (int j = 0; j < table[0].length; j++)
        {
            ans = ans.add(table[0][j]).mod(MOD);
        }

        System.out.println(ans);

        in.close();
    }
}

import java.util.*;
public class tryit{
    public static void main(String[] args) {
        String[] a = new String[101];
        int b[][] = new int [101][101], n, m;
        int sum = 0, x = 1;
        Scanner in = new Scanner(System.in);
        n = in.nextInt();
        m = in.nextInt();
        for(int i = 1 ; i <= n ; i++) {
            a[i] = in.next();
        }
        for(int i = 1 ; i <= n ; i++) {
            for(int j = 1 ; j <= n ; j++) {
                for(int k = 1 ; k <= m ; k++) {
                    if(a[i].charAt(k-1) > a[j].charAt(k-1)) {
                        b[i][j] = b[j][i] = 1;
                    }
                    else b[i][j] = b[j][i] = 0;
                }
            }
        }
        for(int i = 1 ; i <= n ; i++) {
            sum = sum + b[i][1];
            x = x * (n - i);
        }
        for(int i = 2 ; i <= n ; i++) {
            for(int j = 1 ; j < i ; j++) {
                sum = sum + b[j][i];
                x = x * (n - i);
            }
        }
        sum = sum * x;
        System.out.println((sum % 1000000007));
    }
}

import java.io.*;

public class Main {
	private static final int mod = 1000000007;
	private static final int max_n = 100;
	private static final int max_m = 100;

	public static void main(String[] args) throws IOException {
		char[][] names = new char[max_n][max_m];

		BufferedReader reader = new BufferedReader(new InputStreamReader(System.in));
		String[] input = reader.readLine().split(" ");
		int n = Integer.parseInt(input[0]);
		int m = Integer.parseInt(input[1]);
		for (int i = 0; i < n; i++)
			names[i] = reader.readLine().toCharArray();
		reader.close();

		int[] counts = new int[max_m];
		for (int i = 0; i < max_m; i++)
			counts[i] = -1;
		int result = 1;

		for (int k = 1; k <= m; k++) {
			for (int j = 0; j < n - 1; j++)
				for (int i = j + 1; i < n; i++)
					if (names[i][k - 1] < names[j][k - 1]) {
						char temp = names[i][k - 1];
						names[i][k - 1] = names[j][k - 1];
						names[j][k - 1] = temp;
					}
			for (int i = 0; i < n; i++)
				if (counts[names[i][k - 1] - 'A'] == -1)
					counts[names[i][k - 1] - 'A'] = i;
			int temp = 0;
			for (int i = 0; i < max_m; i++) {
				if (counts[i] == -1)
					temp = 0;
				else
					temp = counts[i] + 1;
				counts[i] = -1;
				for (int j = 0; j < temp; j++)
					result = (result * (n - j)) % mod;
			}
		}
		System.out.println(result);
	}
}

