public class Solution {
    private static final String NOTE_H = "H";

    private static String note = NOTE_H;

    private static void changeNote(String n) {
        note = n;
    }

    private static String getNote() {
        return note;
    }

    private static boolean isMajor(int[] chord) {
        // TODO Auto-generated method stub
        return (chord[1] - chord[0] == 4 && chord[2] - chord[1] == 3);
    }

    private static boolean isMinor(int[] chord) {
        // TODO Auto-generated method stub
        return (chord[1] - chord[0] == 3 && chord[2] - chord[1] == 4);
    }

    private static boolean isStrange(int[] chord) {
        return!(isMajor(chord) || isMinor(chord));
    }

    private static boolean isMajorStrict(int[] chord) {
        return (chord[1] - chord[0] == 4 && chord[2] - chord[1] == 3
                &&!isLast(chord[1]) &&!isFirst(chord[1]));
    }

    private static boolean isMinorStrict(int[] chord) {
        return (chord[1] - chord[0] == 3 && chord[2] - chord[1] == 4
                &&!isFirst(chord[2]) &&!isLast(chord[2]));
    }

    private static boolean isStrangeStrict(int[] chord) {
        return!(isMajorStrict(chord) || isMinorStrict(chord));
    }

    private static boolean isFirst(int chord) {
        return chord == 0;
    }

    private static boolean isLast(int chord) {
        return chord == 7;
    }

    private static void printClass(boolean isMajor, boolean isMinor,
            boolean isStrange) {
        if (isMajor) {
            System.out.println("major");
        } else if (isMinor) {
            System.out.println("minor");
        } else if (isStrange) {
            System.out.println("strange");
        } else {
            System.out.println("normal");
        }
    }

    public static void main(String[] args) throws Exception {
        // TODO Auto-generated method stub
        Reader.init(System.in);
        String[] input = Reader.next().split(" ");
        int[] chord = new int[input.length];
        changeNote(input[0]);
        for (int i = 0; i < chord.length; ++i) {
            changeNote(input[i]);
            chord[i] = getNote().hashCode();
        }
        printClass(isMajor(chord), isMinor(chord), isStrange(chord));
        changeNote(NOTE_H);
    }

    static class Reader {
        static void init(InputStream in) {
            scanner = new Scanner(in);
        }

        static Scanner scanner;

        static String next() throws IOException {
            while (!scanner.hasNextLine()) {
                byte[] buffer = new byte[1024];
                scanner.read(buffer);
            }
            return scanner.nextLine();
        }
    }
}

