import java.io.*;
import java.math.BigInteger;
import java.util.*;

public class Solution {
    static long lucky = 4;
    static long start = 4;
    static long len = 4;
    static String s = "";
    static BigInteger n;
    static BigInteger[][] dp = new BigInteger[11][2];

    public static void main(String[] args) throws IOException{
        n = new BigInteger(readLine());
        for (int i = 0; i < 11; i++)
            for (int j = 0; j < 2; j++)
                dp[i][j] = BigInteger.ZERO;
        dp[0][0] = BigInteger.ONE;
        dp[0][1] = n;
        for (int i = 1; i <= 10; i++){
            for (int j = 0; j < 2; j++){
                if (j == 0){
                    dp[i][0] = dp[i - 1][1].divide(BigInteger.valueOf(len));
                    if (dp[i - 1][1].mod(BigInteger.valueOf(len)).compareTo(BigInteger.ZERO) == 0){
                        dp[i][1] = n;
                    }
                    else {
                        dp[i][1] = n;
                        dp[i][1] = dp[i][1].divide(BigInteger.valueOf(len));
                    }
                }
                else {
                    dp[i][0] = dp[i - 1][0].divide(BigInteger.valueOf(lucky));
                    if (dp[i - 1][0].mod(BigInteger.valueOf(lucky)).compareTo(BigInteger.ZERO) == 0){
                        dp[i][1] = BigInteger.ZERO;
                    }
                    else {
                        dp[i][1] = n;
                        dp[i][1] = dp[i][1].divide(BigInteger.valueOf(lucky));
                    }
                }
            }
            if (i!= 10){
                lucky *= 10;
                len *= 10;
                start *= 10;
            }
        }

        if (dp[10][1].equals(BigInteger.ZERO))
            if (dp[10][0].mod(BigInteger.valueOf(4)).compareTo(BigInteger.ZERO) == 0)
                System.out.print("YES");
            else
                System.out.print("NO");
        else {
            if (dp[10][1].mod(BigInteger.valueOf(4)).compareTo(BigInteger.ZERO) == 0){
                System.out.print("NO");
                return;
            }
            if (dp[10][0].mod(BigInteger.valueOf(4)).compareTo(BigInteger.ZERO) == 0){
                System.out.print("YES");
                return;
            }
            else {
                if (dp[10][0].divide(BigInteger.valueOf(4)).mod(BigInteger.valueOf(7)).compareTo(BigInteger.ZERO) == 0)
                    System.out.print("YES");
                else
                    System.out.print("NO");
            }
        }
    }

    private static String readLine() throws IOException {
        InputStream inputStream = System.in;
        InputStreamReader inputStreamReader = new InputStreamReader(inputStream);
        BufferedReader bufferedReader = new BufferedReader(inputStreamReader);
        return bufferedReader.readLine();
    }

}

