    static void solve() {
        String s = sc.next();
        int [] dp=new int [MAX+1];
        for ( int i = 0 ; i <= MAX ; i++ ) {
            for ( int j = 0 ; j <= MAX/2 ; j++ ) {
                if ( s.indexOf( ""+4+j )!= -1 && s.indexOf( ""+4+j+"7" )!= -1 ) {
                    dp[i] = Math.max( dp[i], dp[j] + (s.indexOf( ""+4+j+"7" )-s.indexOf( ""+4+j ))+2 );
                }
                if ( s.indexOf( ""+7+j )!= -1 && s.indexOf( ""+7+j+"4" )!= -1 ) {
                    dp[i] = Math.max( dp[i], dp[j] + (s.indexOf( ""+7+j+"4" )-s.indexOf( ""+7+j ))+2 );
                }
            }
        }
        int cnt = 0, ind = 0 ;
        for ( int i = MAX-1 ; i > 0 ; i-- ) {
