public class plant {
	static long MOD = 1000000007;
	public static void main(String[] args)
	{
		Scanner scan = new Scanner(System.in);
		long n = scan.nextLong();
		long k,n1,n2,r;
		if(n==0)
		{
			System.out.println(1);
			return ;
		}
		k = (n-1)/(2*MOD);
		n1 = n - k*2*MOD;
		n2 = k;
		r = exp(2,n1)%MOD;
		r = mult(r,inv(3))%MOD;
		r = add(r,mult(exp(2,n2),inv(2)))%MOD;
		r = mult(r,inv(5))%MOD;
		System.out.println(r);
	}
	static long add(long a, long b)
	{
		a += b;
		if(a>=MOD)
			a-=MOD;
		return a;
	}
	static long sub(long a, long b)
	{
		a -= b;
		if(a<0)
			a+=MOD;
		return a;
	}
	static long mult(long a, long b)
	{
		long r = (a*b)%MOD;
		return r;
	}
	static long inv(long a)
	{
		long b = MOD, u = 0, v = 1;
		while(b!= 0){
			long t = a/b;
			a = a%b;
			b = t;
			t = u;
			u = u-mult(a,v);
			u = u%MOD;
			v = v-mult(a,u);
			v = v%MOD;
			u = t;
		}
		if(u<0)
			u+=MOD;
		return u;
	}
	static long exp(long a, long n)
	{
		if(n==0)
			return 1;
		long temp = exp(a,n/2);
		temp = mult(temp,temp);
		if(n%2==1)
			temp = mult(temp,a);
		return temp;
	}
}

