		long n;
		Scanner sc = new Scanner(System.in);
		n = sc.nextLong();

		if(n == 0) System.out.println(1);
		else System.out.println(mult(new long[][] {{1, 0}}, expo(new long[][] {{3, 1}, {1, 3}}, n))[0][0]);
	}

	public static long[][] mult(long[][] a, long[][] b) {
		long[][] res = new long[a.length][b[0].length];
		for(int i = 0; i < a.length; i++) {
			for(int j = 0; j < b[0].length; j++) {
				for(int k = 0; k < a[0].length; k++) {
					res[i][j] = (res[i][j] + a[i][k]*b[k][j])%1000000007;
				}
			}
		}
		return res;
	}

	public static long[][] expo(long[][] base, long pow) {
		if(pow == 1) return base;
		long[][] tmp = expo(base, pow/2);
		tmp = mult(tmp, tmp);
		if(pow%2 == 1) tmp = mult(tmp, base);
		return tmp;
	}
}

