    private static class InputReader {
        private final InputStream stream;
        private final byte[] buffer = new byte[1024];
        private int ptr = 0;
        private int buflen = 0;
        private boolean hasNextByte() {
            if (ptr < buflen) {
                return true;
            } else {
                ptr = 0;
                try {
                    buflen = stream.read(buffer);
                } catch (IOException e) {
                    e.printStackTrace();
                }
                if (buflen <= 0) {
                    return false;
                }
            }
            return true;
        }
        private int readByte() { if (hasNextByte()) return buffer[ptr++]; else return -1;}

        private static boolean isPrintableChar(int c) { return 33 <= c && c <= 126;}
        private static boolean isWhiteSpace(int c) { return c =='' || c == '\t' || c == '\n' || c == '\r';}

        private boolean hasNextChar() { while (hasNextByte() &&!isPrintableChar(buffer[ptr])) ptr++; return hasNextByte();}
        private int readChar() { while (hasNextByte() &&!isPrintableChar(buffer[ptr])) ptr++; if (hasNextByte()) return buffer[ptr++]; else return -1;}
        private boolean hasNextInt() { if (!hasNextChar()) return false; int c = readChar(); if (c == -1 || c < '0' || '9' < c) {ptr--; return false;} return true;}
