            if (c < '0' || '9' < c) {ptr--; throw new NumberFormatException();}
            do {n *= 10; n += c - '0'; c = readChar();} while (!(c < '0' || '9' < c)); if (minus) n = -n; return n;}

        private boolean hasNextLong() { if (!hasNextChar()) return false; int c = readChar(); if (c == -1 || c < '0' || '9' < c) {ptr--; return false;} return true;}
        private long readLong() { if (!hasNextLong()) throw new NumberFormatException(); long n = 0; boolean minus = false; int c = readChar();
            if (c == '-') {minus = true; c = readChar();}
            if (c < '0' || '9' < c) {ptr--; throw new NumberFormatException();}
            do {n *= 10; n += c - '0'; c = readChar();} while (!(c < '0' || '9' < c)); if (minus) n = -n; return n;}

