public class Solution {
    public static void main(String[] args){
        int n, m;
        String[] input;
        int[][] table;
        int c_left, c_right;
        int shift = 0;
        int min_shift = 10000000;
        int result = 10000000;
        int index = 0;
        boolean check = false;
        try{
            InputStreamReader isr = new InputStreamReader(System.in);
            BufferedReader br = new BufferedReader(isr);
            StringTokenizer st = new StringTokenizer(br.readLine());
            n = Integer.parseInt(st.nextToken());
            m = Integer.parseInt(st.nextToken());
            table = new int[n][m];
            input = new String[n];
            for(int i=0; i<n; i++){
                input[i] = br.readLine();
            }
            for(int i=0; i<n; i++){
                for(int j=0; j<m; j++){
                    if(input[i].charAt(j) == '1'){
                        c_left = 0;
                        for(int k=0; k<j; k++){
                            c_left = c_left + (input[i].charAt(k) == '1'? 0 : 1);
                        }
                        c_right = 0;
                        for(int k=j+1; k<m; k++){
                            c_right = c_right + (input[i].charAt(k) == '1'? 0 : 1);
                        }
                        if(c_left < c_right){
                            shift = m-j;
                        } else if(c_left > c_right){
                            shift = j;
                        } else {
                            shift = 0;
                        }
                        if(min_shift > shift){
                            min_shift = shift;
                            result = i;
                        } else if(min_shift == shift){
                            if(input[i].indexOf("1") < input[result].indexOf("1")){
                                result = i;
                            }
                        }
                        check = true;
                        break;
                    }
                }
                if(check){
                    break;
                }
            }
            if(check){
                for(int i=0; i<n; i++){
                    for(int j=0; j<m; j++){
                        table[i][j] = input[i].charAt(j+result);
                    }
                }
                for(int i=0; i<n; i++){
                    c_left = 0;
                    for(int j=0; j<m; j++){
                        c_left = c_left + (table[i][j] == '1'? 0 : 1);
                    }
                    if(min_shift > c_left){
                        min_shift = c_left;
                        index = i;
                    }
                }
                for(int i=0; i<n; i++){
                    if(i == index){
                        continue;
                    }
                    for(int j=0; j<m; j++){
                        table[i][j] = table[i][j+min_shift];
                    }
                }
                for(int i=0; i<m-min_shift; i++){
                    table[index][i] = table[index][i+m];
                }
                int count = 0;
                for(int i=0; i<n; i++){
                    for(int j=0; j<m; j++){
                        if(table[i][j] == '1'){
                            count++;
                        }
                    }
                }
                System.out.println(count);
            } else {
                System.out.println(-1);
            }
        } catch (IOException e){
            e.printStackTrace();
        }
    }
}

