        for (int i = 0 ; i < n ; i++) arr[i] = readLine().chars().toArray();
        int mi = (int) 1e9;
        int num = 0;
        for (int j = 0 ; j < m ; j++) {
            num = 0;
            for (int i = 0 ; i < n ; i++) 
                if (arr[i][j] == '1') num++;
            for (int i = 0 ; i < n ; i++) {
                for (int k = 0 ; k < m - 1 ; k++) 
                    arr[i][k] = arr[i][k+1];
                arr[i][m-1] = arr[i][0];
            }
            for (int i = 0 ; i < n - 1 ; i++) {
                int val = 0;
                for (int k = 0 ; k < n ; k++)
                    val += arr[k][j] == '1'? 1 : 0;
                mi = Math.min(mi, val);
            }
        }
