    private void solve() throws IOException {
        Reader.init(System.in);
        char[] pattern = Reader.next().toCharArray();
        char[] answer = Reader.next().toCharArray();

        int ok = 0;
        for (int mask = 0; mask < (1 << pattern.length); ++mask) {
            char[] copy = pattern.clone();
            for (int i = 0; i < pattern.length; ++i) {
                if (mask >> i & 1!= 0) {
                    copy[i] = answer[i];
                }
            }

            boolean isValid = true;
            for (int i = 1; i < copy.length; ++i) {
                if (copy[i - 1] > copy[i]) {
                    isValid = false;
                    break;
                }
            }

            ok |= isValid? 1 : 0;
        }

        System.out.println(ok == 1? "OK" : "WRONG_ANSWER");
    }

