public class Solution implements Runnable {
    private void solve() throws IOException {
        int n = reader.nextInt();
        int arr[] = new int[n];
        for (int i = 0 ; i < n ; i++)
            arr[i] = reader.nextInt();

        int val = 0 ;
        for (int i = 0 ; i < n ; i++) {
            for (int j = 0 ; j < i ; j++) {
                if (arr[i] < arr[j]) {
                    val = arr[i] ;
                    arr[i] = arr[j];
                    arr[j] = val ;
                }
            }
        }

        int count = 0 ;
        val = 0 ;
        for (int i = 1 ; i < n ; i++) {
            if (arr[i-1]!= arr[i]) {
                count ++ ;
                val = arr[i] ;
            }
        }

        if (count >= 2) {
            writer.print(val + "\n");
        } else {
            writer.print("NO\n");
        }
    }

    public static void main(String[] args) throws IOException {
        try (Input reader = new StandardInput(); PrintWriter writer = new PrintWriter(System.out)) {
            new Solution().run();
        }
    }

    StandardInput reader;
    PrintWriter writer;

    @Override
    public void run() {
        try {
            reader = new StandardInput();
            writer = new PrintWriter(System.out);
            solve();
            reader.close();
            writer.close();
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    interface Input extends Closeable {
        String next() throws IOException;
        default int nextInt() throws IOException {
            return Integer.parseInt(next());
        }
        default long nextLong() throws IOException {
            return Long.parseLong(next());
        }
        default double nextDouble() throws IOException {
            return Double.parseDouble(next());
        }
        default int[] readIntArray() throws IOException {
            return readIntArray(nextInt());
        }
        default int[] readIntArray(int size) throws IOException {
            int[] array = new int[size];
            for (int i = 0 ; i < array.length ; i++)
                array[i] = nextInt();
            return array;
        }
        default long[] readLongArray(int size) throws IOException {
            long[] array = new long[size];
            for (int i = 0 ; i < array.length ; i++)
                array[i] = nextLong();
            return array;
        }
    }

    private static class StandardInput implements Input {
        private final BufferedReader reader = new BufferedReader(new InputStreamReader(System.in));
        private StringTokenizer tokenizer;

        private StandardInput() {
            try {
                tokenizer = reader.skip(Integer.MAX_VALUE).tokenize();
            } catch (Exception e) {
                e.printStackTrace();
            }
        }

        @Override
        public void close() throws IOException {
            reader.close();
        }

        @Override
        public String next() throws IOException {
            if (tokenizer == null)
                throw new NoSuchElementException();
            return tokenizer.nextToken();
        }
    }
}

